Ans getchenge(int money){

  Ans now;
  now.data[0] = now.data[1] = now.data[2] = now.data[3] = 0;

  for(int i = 0; i < 4; i++){
    now.data[3-i] = money/mon[3-i];
    money %= mon[3-i];
  }

  return now;
}

Ans getans(int a, int b, int c, int d, Ans p){
  p.data[0] -= a;
  p.data[1] -= b;
  p.data[2] -= c;
  p.data[3] -= d;


  int A = (a*10 + b*50 + c*100+ d*500);

  if(A < pay){
    for(int i = 0; i < 4; i++)
      p.data[i] = INF;
    return p;
  }

  int che = A-pay;
  Ans tmp = getchenge(che);

  for(int i = 0; i < 4; i++)
    p.data[i] += tmp.data[i];
  return p;
}

