        get<0>(coin) = 0;
        get<1>(coin) = 0;
        get<2>(coin) = 0;
        get<3>(coin) = 0;

        int ten, fifty, one_hundred, five_hundred;
        cin >> ten >> fifty >> one_hundred >> five_hundred;
        if (m / 10 % 5 <= ten)
        {
            get<0>(coin) += m / 10 % 5;
            ten -= m / 10 % 5;
            m -= (m / 10 % 5) * 10;
        }
        while (ten >= 5) // && m >= 50
        {
            get<0>(coin) += 5;
            ten -= 5;
            m -= 50;
        }
        if (m % 50 != 0 && fifty >= 1)
        {
            get<1>(coin) += 1;
            fifty--;
            //m -= 50;
            m -= m % 50;
        }
        if (m % 100 == 50 && fifty >= 1)
        {
            get<1>(coin) += 1;
            fifty--;
            m -= 50;
        }
        while (fifty >= 2)
        {
            get<1>(coin) += 2;
            fifty -= 2;
            m -= 100;
        }
        if (m % 100 != 0 && one_hundred >= 1)
        {
            get<2>(coin) += 1;
            one_hundred--;
            m -= m % 100;
        }
        if (m / 100 % 5 <= one_hundred && m / 100 % 5 != 0)
        {
            get<2>(coin) += m / 100 % 5;
            one_hundred -= m / 100 % 5;
            m -= m % 500;
        }
        while (one_hundred >= 5)
        {
            get<2>(coin) += 5;
            one_hundred -= 5;
            m -= 500;
        }
        if (m > 0 && m % 500 == 0 && m / 500 <= five_hundred)
        {
            get<3>(coin) = m / 500;
        }
        else if (m > 0)
        {
            get<3>(coin) = m / 500 + 1;
        }
        answer.push_back(coin);
    }
    for (int i = 0; i < answer.size(); i++)
    {
        if (get<0>(answer.at(i)) != 0)
        {
            cout << 10 << " " << get<0>(answer.at(i)) << endl;
