using namespace std;

int main()
{
    while (true) {
        int n;
        cin >> n;
        if (n == 0) {
            break;
        }
        int cnt[4];
        for (int i = 0; i < 4; i++) {
            cin >> cnt[i];
        }
        int yen[4] = {10, 50, 100, 500};
        int ans[4] = {0, 0, 0, 0};
        int maxi = numeric_limits<int>::min();
        for (int i = 0; i <= cnt[3]; i++) {
            for (int j = 0; j <= cnt[2]; j++) {
                for (int k = 0; k <= cnt[1]; k++) {
                    for (int l = 0; l <= cnt[0]; l++) {
                        int ret = (i * 500 + j * 100 + k * 50 + l * 10) - n;
                        if (ret < 0) {
                            continue;
                        }
                        int sum = 0;
                        for (int i = 3; i >= 0; i--) {
                            sum += ret / yen[i];
                            ret = ret % yen[i];
                        }
                        const int d = i + j + k + l - sum;
                        if (maxi < d) {
                            ans[3] = i;
                            ans[2] = j;
                            ans[1] = k;
                            ans[0] = l;
                            maxi = d;
                        }
                    }
                }
            }
        }
        for (int i = 0; i < 4; i++) {
            if (ans[i] > 0) {
                cout << yen[i] << " " << ans[i] << endl;
            }
        }
        cout << endl;
    }
    return 0;
}

