using namespace std;

int inf = 99999;

int vals[] = {10, 50, 100, 500};

int minuses[4];

void solve(int total, int n[4], int i, int uses[4], int* mincost) {
    if (i == 4) {
        int paysum = 0;
        int cost = 0;
        for (int j = 0; j < 4; ++j) {
            paysum += uses[j] * vals[j];
            cost -= uses[j];
        }
        int returns = paysum - total;
        if (returns < 0) return;

        for (int k = 3; k >= 0; --k) {
            int nret = returns / vals[k];
            returns %= vals[k];
            cost += nret;
        }

        if (cost < *mincost) {
            *mincost = cost;
            for (int k = 0; k < 4; ++k) {
                minuses[k] = uses[k];
            }
        }

        return;
    }

    for (int k = 0; k <= n[i]; ++k) {
        uses[i] = k;
        solve(total, n, i+1, uses, mincost);
    }
}

int main() {
    bool found = false;
