int main() {
    int n;
    int p[4] = {10, 50, 100, 500};
    int c[4], d[4];
    int a[4];

    n = -1;
    while(true) {
        if(n >= 0) cout << endl;
        cin >> n;
        if(n == 0) break;
        int mdec = 80;
        cin >> c[0] >> c[1] >> c[2] >> c[3];
        for(d[0] = 0; d[0] <= c[0]; ++d[0]) {
            for(d[1] = 0; d[1] <= c[1]; ++d[1]) {
                for(d[2] = 0; d[2] <= c[2]; ++d[2]) {
                    for(d[3] = 0; d[3] <= c[3]; ++d[3]) {
                        int dec = -d[0] -d[1] -d[2] -d[3];
                        int change = p[0]*d[0] + p[1]*d[1] + p[2]*d[2] + p[3]*d[3] - n;
                        if(change < 0) {
                            continue;
                        }
                        int m;
                        for(m = 3; m >= 0; --m) {
                            if(d[m] > 0 && change / p[m] > 0) break;
                            dec += change / p[m];
                            change %= p[m];
                        }
                        if(m < 0 && dec < mdec) {
                            mdec = dec;
                            for(int m = 0; m < 4; ++m) {
                                a[m] = d[m];
                            }
                        }
                    }
                }
            }
        }

        for(int i = 0; i < 4; ++i) {
            if(a[i] > 0) {
                cout << p[i] << " " << a[i] << endl;
            }
        }
    }
    return 0;
