int main() {
    while (true) {
        int kakaku[4] = {10,50,100,500};
        int N;
        int maisuu[4];
        cin >> N;
        if (N == 0)return 0;
        rep(i, 4)cin >> maisuu[i];
        int curmoney = N;
        int ans[4]; rep(i, 4)ans[i] =0;
        auto run = [&](int step) {
            curmoney = (curmoney - 1 + kakaku[step]) / kakaku[step] * kakaku[step];
            int hasuu = curmoney % kakaku[step+1];
            int kiriiikazu = hasuu / kakaku[step];
            int kakakuhi=(kakaku[step + 1] / kakaku[step]);
            if (kiriiikazu > maisuu[step])ans[step] = 0;
            else ans[step]=(maisuu[step] - kiriiikazu) / kakakuhi * kakakuhi + kiriiikazu;
            curmoney -= ans[step] * kakaku[step];
            curmoney = max(0, curmoney);
        };
        rep(i, 3)run(i);
        {
            ans[3]=(curmoney+500-1) / 500;
        }
        rep(i, 4) {
            if (ans[i] != 0)
                cout << kakaku[i] << " " << ans[i] << endl;
        }
        cout << endl;
    }
}

