					for (p[3] = 0; p[3] <= num[3]; ++p[3])
					{
						int pay = 0, left[4];
						rep (i, 4)
						{
							pay += v[i] * p[i];
							left[i] = num[i] - p[i];
						}
						if (pay < n)
							continue;

						int t = pay - n;
						for (int i = 3; i >= 0; --i)
						{
							int w = t / v[i];
							left[i] += w;
							t -= v[i] * w;
						}

						int r = accumulate(left, left + 4, 0);
						if (r < res)
						{
							res = r;
							copy(p, p + 4, pay_n);
						}
					}

		for (int i = 0; i < 4; ++i)
			if (pay_n[i])
				cout << v[i] << " " << pay_n[i] << endl;
		cout << endl;
	}
}

