#include<iostream>
#include<algorithm>
#include<vector>
using namespace std;
#define REP(i, a, n) for(int i=a; i<n; i++)
#define RREP(i, a, n) for(int i=n-1; i>=a; i--)

int main() {
	int money;
	int value[4] = { 10, 50, 100, 500 };
	int pay[4];
	int ret[4];

	bool flag = true;
	while (cin >> money, money) {
		if (!flag) cout << endl;
		flag = false;

		int sum = 0;
		int coin[4];

		REP(i, 0, 4) cin >> coin[i];

		int min = 20 * 20 * 20 * 20;
		int minC[4];
		REP(i, 0, 4) minC[i] = 0;
		for (pay[0] = 0; pay[0] <= coin[0]; pay[0]++) {
			for (pay[1] = 0; pay[1] <= coin[1]; pay[1]++) {
				for (pay[2] = 0; pay[2] <= coin[2]; pay[2]++) {
					for (pay[3] = 0; pay[3] <= coin[3]; pay[3]++) {
						REP(i, 0, 4) ret[i] = 0;

						sum = 0;
						REP(i, 0, 4) sum += value[i] * pay[i];
						if (sum < money) continue;

						sum -= money;
						RREP(i, 0, 4) {
							while (sum >= value[i]) {
								sum -= value[i];
								ret[i]++;
							}
						}

						int remain = 0;
						REP(i, 0, 4) remain += coin[i] - pay[i] + ret[i];
						if (remain < min) {
							min = remain;
							REP(i, 0, 4) minC[i] = pay[i];
						}
					}
				}
			}
		}

		REP(i, 0, 4) {
			if (minC[i] > 0) cout << value[i] << " " << minC[i] << endl;
		}

	}
	return 0;
}

