int Ret(int n){
  int retcnt,n10,n50,n100,n500;
  n500=n/500;
  n%=500;
  n100=n/100;
  n%=100;
  n50=n/50;
  n%=50;
  n10=n/10;
  retcnt=n10+n50+n100+n500;
  return retcnt;
}

