struct dataset {
  int req;
  int coin010;
  int coin050;
  int coin100;
  int coin500;
};

int load(dataset* d) {
  int r;
  scanf("%d", &r);
  if( !r ) return 0;
  d->req = r;
  scanf("%d %d %d %d",
        &(d->coin010),
        &(d->coin050),
        &(d->coin100),
        &(d->coin500));
  return 1;
}

void solve(dataset* d) {
  int change = -(d->req);
  change += d->coin010 * 10;
  change += d->coin050 * 50;
  change += d->coin100 * 100;
  change += d->coin500 * 500;

  int r010, r050, r100, r500;

  r500 = change / 500;
  change %= 500;

  r100 = change / 100;
  change %= 100;

  r050 = change / 50;
  change %= 50;

  r010 = change / 10;
  change %= 10;

  if( r010 < d->coin010 ) {
    printf("10 %d\n", d->coin010 - r010);
  }
  if( r050 < d->coin050 ) {
    printf("50 %d\n", d->coin050 - r050);
  }
  if( r100 < d->coin100 ) {
    printf("100 %d\n", d->coin100 - r100);
  }
  if( r500 < d->coin500 ) {
    printf("500 %d\n", d->coin500 - r500);
  }
}

int main() {
  struct dataset ds;
  int needEmptyLine = 0;
  while( load(&ds) ) {
    if( needEmptyLine ) printf("\n");
    solve(&ds);
    needEmptyLine = 1;
  }
  return 0;
}

