	while (cin >> n) {
		if (n == 0) break;
		if (cnt) cout << endl;
		int c[4];
		cin >> c[0] >> c[1] >> c[2] >> c[3];

        int min_mai=1e9;
        Data ans;
        int t = c[0]+c[1]+c[2]+c[3];
		bool judge = false;
		for (int i = 0; i <= c[0]; ++i) {
			for (int j = 0; j <= c[1]; ++j) {
				for (int k = 0; k <= c[2]; ++k) {
					for (int l = 0; l <= c[3]; ++l) {
						int sum = 10 * i + 50 * j + 100 * k + 500 * l;
						if (sum < n) continue;

                        int cnt=t-(i+j+k+l);
						sum = sum - n;

						if (l == 0) {
                            cnt += sum/500;
							sum %= 500;

						}

						if (k == 0 ) {
                            cnt += sum/100;
							sum %= 100;
						}

						if (j == 0 ) {
                            cnt += sum/50;
							sum %= 50;
						}
						if (i == 0 ) {
                            cnt += sum/10;
							sum %= 10;
						}

                        if(sum == 0 &&min_mai>cnt){
                            min_mai = cnt;
                            ans = {i, j, k, l};
                        }
                    }
				}

			}
		}

