    while(cin >> bil && bil)
    {
        if(sec) cout << endl;
        int w[4] = {}, p[4],  minw[] = {MAXC, MAXC, MAXC, MAXC}, minwsum = MAXC * 4;
        REP(4) cin >> w[i] ;

        for(p[0] = 0; p[0] <= w[0]; p[0]++)for(p[1] = 0;p[1] <= w[1]; p[1]++)
            for(p[2] = 0; p[2] <= w[2]; p[2]++)for(p[3] = 0; p[3] <= w[3]; p[3]++)
            {
                int sumpay = 0; REP(4) sumpay += p[i] * COIN[i];
                int r[4] = {}; 
                rest = sumpay - bil;
                if(rest < 0) continue;

                for(int j=3;j>=0;j--){ r[j] = rest /COIN[j]; rest = rest % COIN[j]; }

                int muda = 0;
                REP(4)if(p[i] * r[i] > 0){ muda = 1;}
                if(muda ) continue;

                int sumc = 0; REP(4) sumc += w[i] - p[i] + r[i];
                if(sumc < minwsum)
                {
                    REP(4) minw[i] = w[i] - p[i] + r[i];
                    minwsum = sumc;
                }
            }
        REP(4)if(w[i] - minw[i] > 0) cout << COIN[i] << " " << w[i] - minw[i] << endl;

        sec = 1;
    }

    return 0;
}

