int solve(int m, int i, int j, int k, int l) {
  int total = i * 10 + j * 50 + k * 100 + l * 500; 
  if( total < m ) return -1;
  int diff = total - m;
  int res = 0;
  res += (diff / 10 % 10) % 5 + (diff / 10 % 10) / 5;
  res += (diff / 100 % 10) % 5 + (diff / 100) / 5;
  return res;
}


auto main() -> int {

  for(int t = 0; ; ++t) {
    int m;
    std::cin >> m;
    if( m == 0 ) break;

    int a, b, c, d;
    std::cin >> a >> b >> c >> d;

    int res[4] = {};
    int res2 = INF;

    for(int i = 0; i <= a; ++i) {
      for(int j = 0; j <= b; ++j) {
        for(int k = 0; k <= c; ++k) {
          for(int l = 0; l <= d; ++l) {
            int x;
            if( (x = solve(m, i, j, k, l)) >= 0 ) {
              if( res2 > a + b + c + d - i - j - k - l + x ) {
                res2 = a + b + c + d - i - j - k - l + x;
                res[0] = i;
                res[1] = j;
                res[2] = k;
                res[3] = l;
              }
            }
          }
        }
      }
    }

    if( t != 0 ) std::cout << std::endl;
    if( res[0] != 0 ) std::cout << 10 << ' ' << res[0] << std::endl;
    if( res[1] != 0 ) std::cout << 50 << ' ' << res[1] << std::endl;
    if( res[2] != 0 ) std::cout << 100 << ' ' << res[2] << std::endl;
    if( res[3] != 0 ) std::cout << 500 << ' ' << res[3] << std::endl;
  }

  return 0;
}

