void solve()
{
	const int _coinTypes[] = { 10, 50, 100, 500 };
	const int C = countof(_coinTypes);
	const auto coinTypes = vector<int>(_coinTypes, _coinTypes + C);
	REP (testcase, INF)
	{
		READ(int, price);
		if (!price) { break; }
		if (testcase != 0) { cout << endl; }

		vector<int> money = read<int>(coinTypes.size());
		auto value = inner_product(ALLOF(money), coinTypes.begin(), 0);

		vector<int> charge;
		int cvalue = value - price;
		RREP(i, C) { charge.push_back(cvalue / coinTypes[i]); cvalue %= coinTypes[i]; }
		reverse(ALLOF(charge));

		vector<int> payment;
		REP(i, C) { charge.push_back(max(money[i] - charge[i], 0)); }
