using namespace std;

class coin{
public:
	int a,b,c,d,rest;
	coin(int n,int _a,int _b,int _c,int _d){
		a=_a;
		b=_b;
		c=_c;
		d=_d;
		rest=10*a+50*b+100*c+500*d-n;
	};

	int restNum(){
		int tmp=rest,res=0;
		res+=tmp/500;
		tmp%=500;
		res+=tmp/100;
		tmp%=100;
		res+=tmp/50;
		tmp%=50;
		res+=tmp/10;

		return res;
	}
};


int main()
{
	int n,a,b,c,d;
	while(cin>>n){
		if(n==0) break;
		cin>>a>>b>>c>>d;
		coin ans = coin(n,a,b,c,d);

		for(int aa=0;aa<=a;aa++){
			for(int bb=0;bb<=b;bb++){
				for(int cc=0;cc<=c;cc++){
					for(int dd=0;dd<=d;dd++){
						coin tmp = coin(n,aa,bb,cc,dd);
						if(tmp.rest>=0 && ans.restNum()>tmp.restNum()) ans=tmp;
					}
				}
			}
		}
		if(ans.a!=0) cout<<10<<" "<<ans.a<<endl;
		if(ans.b!=0) cout<<50<<" "<<ans.b<<endl;
		if(ans.c!=0) cout<<100<<" "<<ans.c<<endl;
		if(ans.d!=0) cout<<500<<" "<<ans.d<<endl;
		cout<<endl;
	}


	return 0;
}

