    ret += tmp;
    arg -= tmp * 500;
    tmp = arg / 100;
    ret += tmp;
    arg -= tmp * 100;
    tmp = arg / 50;
    ret += tmp;
    arg -= tmp * 50;
    tmp = arg / 10;
    ret += tmp;
    return ret;
}

