const int MAX = 200000;
const double PI = 3.14159265358979323846;
const double EPS = 1e-12;
const int INF = 1<<29;
const int MOD = 1000000007;

const int coins[] = {10, 50, 100, 500};
int use[4], cnt[4];

void solve(void){
    int N;
    while (1) {
        cin >> N;
        if (N == 0) break;
        rep(4, i) cin >> cnt[i];

        rep(3, c) {
            use[c] = min((N%coins[c+1])/coins[c], cnt[c]);
            N -= coins[c]*use[c];
        }
        use[3] = min(N/coins[3], cnt[3]);
        N -= coins[3]*use[3];
        repr(4, i) {
            if (N <= 0) break;
            if (use[i] == cnt[i]) continue;
            use[i] += (N-1) / coins[i] + 1;
            N -= ((N-1) / coins[i] + 1) * coins[i];
        }

        rep(4, i) {
            if (use[i]) printf("%d %d\n", coins[i], use[i]);
        }
        printf("\n");

        repr(4, j) {
            Repr(j-1, 0, i) {
                int dif = (cnt[i] - use[i])/(coins[j]/coins[i]);
                dif = min(dif, use[j]);
                use[i] += dif * (coins[j]/coins[i]);
                use[j] -= dif;
            }
        }

        rep(4, i) {
            if (use[i]) printf("%d %d\n", coins[i], use[i]);
        }

        printf("\n");
    }
}

int main(void){
  solve();
  return 0;
}

