using namespace std;

int reverse_coin(int c){
    int cnt=0;
    while(1){
        if(c == 0){
            break;
        }
        if(500 <= c){  // c  =600
            c-=500;
            cnt++;
        }else if(100 <= c){
            c-=100;
            cnt++;
        }else if(50 <= c){
            c-=50;
            cnt++;
        }else if(10 <= c){
            c-=10;
            cnt++;
        }
    }
    return cnt;
}
