/* constant */

const int CS[] = {1, 5, 10, 50};

/* typedef */

/* global variables */

int ns[4], ds[4], minds[4];

/* subroutines */

int change(int pr) {
  int count = 0;
  for (int i = 3; i >= 0; i--) {
    int d = pr / CS[i];
    pr -= d * CS[i];
    count += d;
  }

  return count;
}

/* main */

int main() {
  for (bool first = true;; first = false) {
    int pr;
    cin >> pr;
    if (pr == 0) break;
    pr /= 10;

    for (int i = 0; i < 4; i++)
      cin >> ns[i], ds[i] = 0, minds[i] = 25;

    int minsum = 100;

    for (;;) {
      int pay = 0;
      for (int i = 0; i < 4; i++)  pay += CS[i] * ds[i];

      if (pay >= pr) {
	int count = 0;
	for (int i = 0; i < 4; i++)  count += ns[i] - ds[i];
	count += change(pay - pr);
	if (minsum > count) {
	  minsum = count;
	  for (int i = 0; i < 4; i++)  minds[i] = ds[i];
	}
      }

      int k = 0;
      while (k < 4) {
	ds[k]++;
	if (ds[k] <= ns[k]) break;
	ds[k++] = 0;
      }
      if (k >= 4) break;
    }

    if (! first) putchar('\n');

    for (int i = 0; i < 4; i++) {
      if (minds[i] > 0)
	printf("%d %d\n", CS[i] * 10, minds[i]);
    }
  }

  return 0;
}

