        }
        while (ten >= 5)
        {
            if (m >= 50)
            {
                get<0>(coin) += 5;
                ten -= 5;
                m -= 50;
            }
            else
            {
                get<0>(coin) += m / 10;
                m -= get<0>(coin) * 10;
                break;
            }
        }
        while (fifty >= 2)
        {
            if (m >= 100)
            {
                get<1>(coin) += 2;
                fifty -= 2;
                m -= 100;
