#include <algorithm>
#include <iostream>
#include <limits>
#include <tuple>

using namespace std;

int main()
{
    bool first = false;
    while (true) {
        int cost;
        cin >> cost;
        if (cost == 0) {
            return 0;
        }
        if (first) {
            cout << endl;
        }

        int n10, n50, n100, n500;
        cin >> n10 >> n50 >> n100 >> n500;

        auto changeCoins = [](int n) {
            int nn500 = n / 500;
            n %= 500;
            int nn100 = n / 100;
            n %= 100;
            int nn50 = n / 50;
            n %= 50;
            int nn10 = n / 10;
            return make_tuple(nn10, nn50, nn100, nn500);
        };

        auto numberOfCoins = [](const auto &t) {
            int x, y, z, w;
            tie(x, y, z, w) = t;
            return x + y + z + w;
        };

        auto min_ =
            make_tuple(numeric_limits<int>::max(), numeric_limits<int>::max(),
                       numeric_limits<int>::max(), numeric_limits<int>::max(),
                       numeric_limits<int>::max());
        for (int i = 0; i <= n10; ++i) {
            for (int j = 0; j <= n50; ++j) {
                for (int k = 0; k <= n100; ++k) {
                    for (int l = 0; l <= n500; ++l) {
                        const int sum = i * 10 + j * 50 + k * 100 + l * 500;
                        if (sum < cost) {
                            continue;
                        }
                        const auto change = sum - cost;
                        const auto t = changeCoins(change);
                        if (t == make_tuple(i, j, k, l)) {
                            continue;
                        }
                        auto remainNumberOfCoins = numberOfCoins(t) +
                                                   (n10 - i) + (n50 - j) +
                                                   (n100 - k) + (n500 - l);
                        if (remainNumberOfCoins < get<0>(min_)) {
                            min_ = make_tuple(remainNumberOfCoins, i, j, k, l);
                        }
                    }
                }
            }
        }
        int x, y, z, w;
        tie(ignore, x, y, z, w) = min_;
        if (x != 0) {
            cout << 10 << ' ' << x << endl;
        }
        if (y != 0) {
            cout << 50 << ' ' << y << endl;
        }
        if (z != 0) {
            cout << 100 << ' ' << z << endl;
        }
        if (w != 0) {
            cout << 500 << ' ' << w << endl;
        }
        first = true;
    }
}

