void solve(int cost) {
    vector<int> coin(4);
    rep(i,4) cin>>coin[i];

    int tmp = 114514;
    int x=0,y=0,z=0,w=0;
    rep(i,coin[0]+1) rep(j,coin[1]+1) rep(k,coin[2]+1) rep(l,coin[3]+1) {
        int c = 10*i + 50*j + 100*k + 500*l;
        if(c < cost) continue;

        int dif = c - cost; // otsuri
        int get = 0;
        while(dif > 0) {
            if(dif >= 500)
                get++, dif -=500;
            else if(dif >= 100)
                get++, dif -=100;
