int C(int t){
  int res = 0,a,b,c;
  a = t/500;
  res += a;
  t -= a*500;
  b = t/100;
  res += b;
  t -= b*100;
  c = t/50;
  res += c;
  t -= c*50;
  res += t/10;
  return res;
}
