int toC(int i){
  switch(i){
    case 0:return 10;
    case 1:return 50;
    case 2:return 100;
    case 3:return 500;
    default:return INF;
  }
}


vector<pair<int,int> > solve(){
  vector<pair<int,int> > res;
  vector<int> turi;
  turi.resize(4);
  int sum=0;
  for(int i=0;i<4;i++){
    sum+=c[i]*toC(i);
  }
  sum-=pay;
  for(int i=4;i--;){
