    turi[i]=sum/toC(i);
    sum=sum%toC(i);
  }
  for(int i=0;i<4;i++){
    if(0<c[i]-turi[i]){
      res.push_back(make_pair(toC(i),c[i]-turi[i]));
    }
  }

  sort(res.begin(),res.end());
  return res;
}

