int n;
int c[] = {10,50,100,500};
int k[4];
int a[4];
int main(void){
  bool f = false;
  while(cin >> n){
    if(!n) break;
    if(f) cout << endl;
    f = true;
    memset(a,0,sizeof(a));
    for(int i = 0; i < 4; i++){
      cin >> k[i];
      if(!n) continue;
      if(n >= c[i]){
	int t = min(k[i],n/c[i]);
	k[i] -= t;
	a[i] += t;
	n -= t * c[i];
      }
      if(n && k[i]){
	int m = c[i] - n;
	n = 0;
	k[i]--;
	a[i]++;
	for(int j = i-1; j >= 0; j--){
	  k[j] += m/c[j];
	  m %= c[j];
	}
      }
    }

    for(int i = 0; i < 3; i++){
      if(c[i]*k[i] >= c[i+1]){
	int t = k[i] / (c[i+1]/c[i]);
	k[i+1] += t;
	a[i] += t * (c[i+1]/c[i]);
      }
    }

    for(int i = 0; i < 4; i++){
      if(a[i]){
	cout << c[i] << " " << a[i] << endl;
