#define rep(i,n) for(int i=0;i<n;i++)
int money[4] = {10,50,100,500}; 
int data[4],pay[4],ans[4],use[4];
int cost;
int minans;
int n,I=0;
bool check(int cst){
	for(int i=3;i>=0;i--){
		pay[i] = 0;
		while(cst >= money[i]){
			pay[i]++;
			cst -= money[i];
		}
	}
	rep(i,4)if(pay[i] && use[i]) return false;

	return true;
}
void dfs(int pos){
	if(pos == -1){	
		if(cost >= n && check(cost-n)){
			int kans=0;
			rep(i,4)kans += pay[i] + data[i]-use[i];
			if(kans < minans){
				rep(i,4)ans[i] = use[i];
				minans = kans;
			}
		}
		return;
	}
	for(int i=data[pos];i>=0;i--){
		use[pos] = i;
		cost += i * money[pos];
		dfs(pos-1);
		cost -= i * money[pos];
	}
}
int main(){
	check(100);
	while(cin >> n,n){
		if(I++)cout << endl;
		minans = 10000;
		cost = 0;
		cin >> data[0] >> data[1] >> data[2] >> data[3];
		dfs(3);
		rep(i,4){
			if(ans[i])cout << money[i] << " " << ans[i] << endl;
		}
	}
}

