typedef int Coins[4];

int coins[] = {10, 50, 100, 500};

int S;
int sum;
int ans;
Coins nums;
vector<int> anscs;

void GetChange(int v, int *cp) {
  for (int i=3; i>=0; i--) {
    cp[i] = v/coins[i];
    v %= coins[i];
  }
}

void Rec(int idx, int total, vector<int> &cs) {
  if (idx == 4) {
    if (total < S) return;

    Coins res;
    GetChange(total-S, res);

    int df = 0;
    for (int i=0; i<4; i++) {
      if (cs[i] && res[i]) return;
      df += res[i]-cs[i];
    }

    if (ans > sum+df) {
      ans = sum+df;
      anscs = cs;
    }

    return;
  }

  for (int i=0; i<=nums[idx]; i++) {
    cs.push_back(i);
    Rec(idx+1, total+coins[idx]*i, cs);
    cs.pop_back();
  }
}

