int coin[4] = { 10, 50, 100, 500 };

void pay(int bill, int payment, int change_coin[4])
{
	assert(payment >= bill);
	int change = payment - bill;
	FOR (i, 3, -1) {
		change_coin[i] = change / coin[i];
		change %= coin[i];
	}
}

int main()
{
	std::ios_base::sync_with_stdio(false);
	int bill;
	while (cin >> bill, bill) {
		int n_coin[4];
		int min_n_coins = 100, ans[4] = {0};
		REP (i, 4) {
			cin >> n_coin[i];
		}
		FOR (i, n_coin[0], -1) {
			FOR (j, n_coin[1], -1) {
				FOR (k, n_coin[2], -1) {
					FOR (l, n_coin[3], -1) {
						int sum = coin[0] * i + coin[1] * j + coin[2] * k + coin[3] * l;
						if (sum >= bill) {
							int change_coin[4] = {0};
							pay(bill, sum, change_coin);
							if ((i && change_coin[0]) || (j && change_coin[1]) || (k && change_coin[2]) || (l && change_coin[3])) {
								continue;
							}
							int n_coins = - (i+j+k+l);
							REP (z, 4) {
								n_coins += n_coin[z] + change_coin[z];
							}
							if (n_coins < min_n_coins) {
								min_n_coins = n_coins;
								ans[0] = i, ans[1] = j, ans[2] = k, ans[3] = l;
							}
						}
					}
				}
			}
		}
		REP (i, 4) {
			if (ans[i]) {
				cout << coin[i] << " " << ans[i] << endl;
			}
		}
		cout << endl;
	}
}

