signed main(){

    while(1){
        int a;
        cin>> a;
        if(!a) break;
        int hoge[]={10, 50, 100, 500};
        int geho[4];
        for(int i=0; i<4; i++){
            cin>> geho[i];
        }
        vector<int> coin;
        for(int i=0; i<4; i++){
            for(int j=0; j<geho[i]; j++){
                coin.push_back(hoge[i]);
            }
        }

        int n=coin.size();
        int mimi=30;
        vector<int> an;
        for(int i=0; i<(1<<n); i++){
            vector<int> u;
            for(int j=0; j<n; j++){
                if((i>>j)&1) u.push_back(coin[j]);
            }
            if(mimi<u.size()) continue;
            int oturi=accumulate(u.begin(), u.end(), 0)-a;
            if(oturi<0) continue;
            int b=0;
            for(int i=3; i>=0; i--){
                b+=oturi/hoge[i];
                oturi%=hoge[i];
            }
            if(mimi>n-u.size()+b){
                mimi=n-u.size()+b;
                an=u;
            }
        }

        map<int, int> mp;
        for(int i=0; i<an.size(); i++){
            mp[an[i]]++;
        }
        for(int i=0; i<4; i++){
            if(mp[hoge[i]]){
                cout<< hoge[i]<< " "<< mp[hoge[i]]<< endl;
            }
        }
    }


    return 0;
}

