#include<iostream>
using namespace std;

int remi(int cost) {
  int ans = 0;
  ans += cost/500;
  cost %= 500;
  ans += cost/100;
  cost %= 100;
  ans += cost/50;
  cost %= 50;
  ans += cost/10;
  return ans;
}

int main() {
  int coin[4], use[4], cost, f=0;
  int ccoin[4] = { 10, 50, 100, 500 };
  while(cin>>cost, cost) {
    if(f) cout<<endl;
    cin>>coin[0]>>coin[1]>>coin[2]>>coin[3];
    int ans = 1000000;
    use[0] = use[1] = use[2] = use[3] = 0;
    int sum = coin[0]+coin[1]+coin[2]+coin[3];
    for(int i=0; i<=coin[0]; ++i) {
      for(int j=0; j<=coin[1]; ++j) {
	for(int k=0; k<=coin[2]; ++k) {
	  for(int l=0; l<=coin[3]; ++l) {
	    int ca = i*10+j*50+k*100+l*500;
	    if(ca < cost) continue;
	    int ta = sum-i-j-k-l+remi(ca-cost);
	    if(ans > ta) {
	      use[0] = i,use[1] = j,use[2] = k,use[3] = l;
	      ans = ta;
	    }
	  }
	}
      }
    }
    for(int i=0; i<4; ++i)
      if(use[i]) cout<<ccoin[i]<<" "<<use[i]<<endl;
    f++;
  }
}

