int main()
{
    int m;
    vector<tuple<int, int, int, int>> answer;
    while (cin >> m && m != 0)
    {
        tuple<int, int, int, int> coin; // = {0, 0, 0, 0};
        get<0>(coin) = 0;
        get<1>(coin) = 0;
        get<2>(coin) = 0;
        get<3>(coin) = 0;
        int ten, fifty, one_hundred, five_hundred;
        cin >> ten >> fifty >> one_hundred >> five_hundred;
        if (m / 10 % 5 <= ten)
        {
            get<0>(coin) += m / 10 % 5;
            ten -= m / 10 % 5;
            m -= (m / 10 % 5) * 10;
        }
        while (ten >= 5)
        {
            if (m >= 50)
            {
                get<0>(coin) += 5;
                ten -= 5;
                m -= 50;
            }
            else
            {
                get<0>(coin) += m / 10;
                m -= (m / 10) * 10;
                break;
            }
        }
        while (fifty >= 2)
        {
            if (m >= 100)
            {
                get<1>(coin) += 2;
                fifty -= 2;
                m -= 100;
            }
            else if (m == 50)
            {
                get<1>(coin) += 1;
                m -= get<1>(coin) * 50;
                break;
            }
            else
            {
                break;
            }
        }
        if (fifty == 1 && m % 100 == 50)
        {
            get<1>(coin) += 1;
            m -= 50;
        }
        if (m / 100 % 5 <= one_hundred)
        {
            get<2>(coin) += m / 100 % 5;
            one_hundred -= m / 100 % 5;
            m -= get<2>(coin) * 100;
        }
        while (one_hundred >= 5)
        {
            if (m >= 500)
            {
                get<2>(coin) += 5;
                one_hundred -= 5;
                m -= 500;
            }
            else if (m != 0)
            {
                get<2>(coin) += m / 100;
                m -= (m / 100) * 100;
                break;
            }
            else
            {
                break;
            }
        }
        if (m > 0 && m <= 100 && one_hundred > 0)
        {
            get<2>(coin)++;
            m = 0;
        }
        if (m != 0 && m % 500 == 0 && m / 500 <= five_hundred)
        {
            get<3>(coin) = m / 500;
        }
        else if (m > 0)
        {
            get<3>(coin) = m / 500 + 1;
        }
        answer.push_back(coin);
    }
    for (int i = 0; i < answer.size(); i++)
    {
        if (get<0>(answer.at(i)) != 0)
        {
            cout << 10 << " " << get<0>(answer.at(i)) << endl;
        }
        if (get<1>(answer.at(i)) != 0)
        {
            cout << 50 << " " << get<1>(answer.at(i)) << endl;
        }
        if (get<2>(answer.at(i)) != 0)
        {
            cout << 100 << " " << get<2>(answer.at(i)) << endl;
        }
        if (get<3>(answer.at(i)) != 0)
        {
            cout << 500 << " " << get<3>(answer.at(i)) << endl;
        }
        cout << endl;
    }
    return 0;
}

