using namespace std;
int coin[4] = {10, 50, 100, 500};

int main(){
  int p;
  int counter = 0;
  while(cin >> p and p){
    if(counter++){
      cout << endl;
    }
    p /= 10;
    int c[4];
    REP(i, 4) cin >> c[i];
    int res[4] = {0};
    int mincoin = INF;
    REP(i, c[0]+1) REP(j, c[1]+1) REP(k, c[2]+1) REP(l, c[3]+1){
      int coins = c[0] - i + c[1] - j + c[2] - k + c[3] - l;
      int sum = i + j * 5 + k * 10 + l * 50;
      if(sum < p) continue;
      int ret = sum - p;
