                            {
                                get<0>(coin) = i;
                                get<1>(coin) = j;
                                get<2>(coin) = k;
                                get<3>(coin) = l;
                                max = used_coin;
                            }
                            change = 0;
                            used_coin = 0;
                        }
                    }
                }
            }
        }
        answer.push_back(coin);
    }
    for (int i = 0; i < answer.size(); i++)
    {
        if (get<0>(answer.at(i)) != 0)
        {
            cout << 10 << " " << get<0>(answer.at(i)) << endl;
        }
        if (get<1>(answer.at(i)) != 0)
        {
            cout << 50 << " " << get<1>(answer.at(i)) << endl;
        }
        if (get<2>(answer.at(i)) != 0)
        {
            cout << 100 << " " << get<2>(answer.at(i)) << endl;
        }
        if (get<3>(answer.at(i)) != 0)
        {
            cout << 500 << " " << get<3>(answer.at(i)) << endl;
        }
        if (i != answer.size() - 1)
        {
            cout << endl;
        }
    }
    return 0;
}

