using namespace std;
int coin[]={10,50,100,500,1000};
int main(){
    int n;
    bool flag=false;
    while(scanf("%d",&n),n){
        if(flag)puts("");
        else flag=true;
        int c[4];
        for(int i=0;i<4;i++)scanf("%d",&c[i]);
        for(int i=0;i<4;i++){
            int cnt=0;
            while((n%coin[i+1]||i==3)&&c[i]&&n>0){
                c[i]--;
                cnt++;
                n-=coin[i];
            }
            int x=coin[i+1]/coin[i];
            while(c[i]>=x&&n>0){
                c[i]-=x;
                cnt+=x;
                n-=coin[i]*x;
            }
            while(c[i]>=x){
                c[i]-=x;
                cnt+=x;
            }
            if(cnt)printf("%d %d\n",coin[i],cnt);
        }
    }
    return 0;
}

