#define lpin(a,i,n) for(int i=0;i<n;i++){cin>>a[i];}
int ans=100;
int aa,ab,ac,ad;
void calc(int a,int b,int c,int d,int n,int now){
  if(now>=n){
    int mem,mem2;
    mem=now-n;
    if(ans>mem/500+mem%500/100+mem%100/50+mem%50/10){
      ans=mem/500+mem%500/100+mem%100/50+mem%50/10;
      aa=a;ab=b;ac=c;ad=d;
    }
  }
  if(a==0&&b==0&&c==0&&d==0) return;
  if(a>0) calc(a-1,b,c,d,n,now+10);
  if(b>0) calc(a,b-1,c,d,n,now+50);
  if(c>0) calc(a,b,c-1,d,n,now+100);
  if(d>0) calc(a,b,c,d-1,n,now+500);
}

int main(){
  while(1){
    int n;
    cin>>n;
    if(n==0) break;
    int a,b,c,d;
    cin>>a>>b>>c>>d;
    ans=100;
    calc(a,b,c,d,n,0);
    if(a-aa>0) cout<<"10 "<<a-aa<<endl;
    if(b-ab>0) cout<<"50 "<<b-ab<<endl;
    if(c-ac>0) cout<<"100 "<<c-ac<<endl;
    if(d-ad>0) cout<<"500 "<<d-ad<<endl;
  }
  return 0;
}

