						now -= u*mm[0] + i*mm[1] + o*mm[2] + p*mm[3];
						if (now<0)break;
						int NOW[4] = { -1, -1, -1, -1 };
						int nowans = k;
						nowans -= u + i + o + p;
						for (int l = 3; l >=0; l--)
						{
							NOW[l]=now / mm[l];
							nowans += NOW[l];
							now -= NOW[l] * mm[l];
						}
						if (nowans < ans)
						{
							if (NOW[0] != 0)if (u)break;
							if (NOW[1] != 0)if (i)break;
							if (NOW[2] != 0)if (o)break;
							if (NOW[3] != 0)if (p)break;

							ans = nowans;
							ANS[0] = u;
							ANS[1] = i;
							ANS[2] = o;
							ANS[3] = p;
						}
					}
				}
			}
		}

		for (int i = 0; i < 4; i++)
		{
			if (ANS[i]!=0)
			{
				cout << mm[i] << " " << ANS[i] << endl;

			}
		}
		cout << endl;

	}


}

