		int a, b, c, d;
		cin >> a >> b >> c >> d;
		int answer = INF;
		int aa = 0, ab = 0, ac = 0, ad = 0;
		for(int i = 0; i <= a; ++i){
			for(int j = 0; j <= b; ++j){
				for(int k = 0; k <= c; ++k){
					for(int l = 0; l <= d; ++l){
