#define MAX ((1+5+10+50)*20)
using namespace std;

int price;
int coin[4];
int coin_value[] = {1,5,10,50};
int ansCoin[4];
int otsuri[MAX+1];
int mini;

int sum(int coin[4]) {
  int sum = 0;
  for(int i = 0; i < 4; i++) {
    sum += coin[i];
  }
  return sum;
}

void rec(int now, int p) {
  static int nowCoin[4];
  if(now == price) {
    mini = 100000;
    for(int i = 0; i < 4; i++) nowCoin[i] = 0;
  }

  if(now <= 0) {
    int newValue = sum(nowCoin) + otsuri[-now];
    if(newValue < mini) {
      mini = newValue;
      for(int i = 0; i < 4; i++) ansCoin[i] = nowCoin[i];
    }
    return;
  }
  for(; p < 4; p++) {
    if(nowCoin[p] == coin[p]) continue;
    nowCoin[p]++;
    rec(now - coin_value[p], p);
    nowCoin[p]--;
  }
}

main() {
  for(int i = 0; i <= MAX; i++) {
    int p = i;
    otsuri[i] = 0;
    for(int j = 4-1; j >= 0; j--) {
      otsuri[i] += p / coin_value[j];
      p %= coin_value[j];
    }
  }

  bool flag = false;
  while(1) {
    cin >> price;
    if(price == 0) break;
    price /= 10;

    for(int i = 0; i < 4; i++)
      cin >> coin[i];

    rec(price, 0);

    if(!flag) flag = true;
    else      cout << endl;
    for(int i = 0; i < 4; i++) {
      if(ansCoin[i] == 0) continue;
      cout << coin_value[i] * 10 << ' ' << ansCoin[i] << endl;
    }
  }
}

