inline int calc(int a,int b,int c,int d){
    return a*10+b*50+c*100+d*500;
}

int val(int arg){
    int ret = 0, tmp;
    tmp = arg / 500;
    ret += tmp;
    arg -= tmp * 500;
    tmp = arg / 100;
    ret += tmp;
    arg -= tmp * 100;
    tmp = arg / 50;
    ret += tmp;
    arg -= tmp * 50;
    tmp = arg / 10;
    ret += tmp;
    return ret;
}

