int main(void) {
  while(cin >> N, N) {
    cin >> A >> B >> C >> D;

    int aa = A, ab = B, ac = C, ad = D, n = A + B + C + D;
    REP(a, 0, A + 1) REP(b, 0, B + 1) REP(c, 0, C + 1) REP(d, 0, D + 1) {
      int s = a * 10 + b * 50 + c * 100 + d * 500;
