        int ka = A - a + cha;
        int kb = B - b + chb;
        int kc = C - c + chc;
        int kd = D - d + chd;

        if(n > ka + kb + kc + kd) {
          aa = a, ab = b, ac = c, ad = d;
          n = ka + kb + kc + kd;
        }
