        coin[0] = make_pair(10, num[0]);
        coin[1] = make_pair(50, num[1]);
        coin[2] = make_pair(100, num[2]);
        coin[3] = make_pair(500, num[3]);

        /* 小さい値のコインから払えるだけ払う
        ただし，払った後の残り値が次に大きなコインの値で割り切れるようにする */

        REP(i, 3) {
            int judge;

            for (int j = num[i]; j >= 0; j--) {
                judge = n - j * coin[i].first;
                if (judge % coin[i+1].first == 0) {
                    n -= j * coin[i].first;
                    coin[i].second = j; 

                    break;
                }
            }
        }

        coin[3].second = n / coin[3].first;

        REP(i, 4)   
            if (coin[i].second != 0)    cout << coin[i].first << " " << coin[i].second << endl;
    }
}

