        int res[4] = {20, 20, 20, 20}, rsum = 80;
        int st[4] = {d, c, b, a};

        for (int i = n; i <= 20000; i += 10){
            int tmp[4] = {d, c, b, a};
            int x = i;
            for (int j = 0; j < 4; j++){
                while (tmp[j] && x >= mat[j]){
                    x -= mat[j];
                    tmp[j]--;
                }
            }
            if (!x){
                x = i - n;
                int tsum = 0;
                for (int j = 0; j < 4; j++){
                    while (x >= mat[j]){
                        x -= mat[j];
                        if (tmp[j] < st[j]) tmp[j]++;
                        else tsum++;
                    }
                    tsum += tmp[j];
                }
                if (tsum < rsum){
                    rsum = tsum;
                    memcpy(res, tmp, sizeof(tmp));
                }
            }
        }

        for (int i = 3; i >= 0; i--){
            if (st[i] - res[i] > 0) printf("%d %d\n", mat[i], st[i] - res[i]);
