int main() {
    int p;
    while(scanf("%d",&p),p) {
        int sum=0;
        int cnt[4];
        for(int i=0;i<4;i++) {scanf("%d",&cnt[i]);sum+=cnt[i];}
        int ans[4],best=114514;
        for(int i=0;i<=cnt[0];i++) {
            for(int j=0;j<=cnt[1];j++) {
                for(int k=0;k<=cnt[2];k++) {
                    for(int l=0;l<=cnt[3];l++) {
                        int tot=i*10+j*50+k*100+l*500;
                        if(tot<p) continue;
                        int tmp=sum-i-j-k-l;
                        int change=tot-p;
                        tmp+=change/500;change%=500;
                        tmp+=change/100;change%=100;
                        tmp+=change/50;change%=50;
                        tmp+=change/10;
                        if(best>tmp) {
                            best=tmp;
                            ans[0]=i,ans[1]=j,ans[2]=k,ans[3]=l;
                        }
                    }
                }
            }
        }
        if(ans[0]>0) printf("10 %d\n",ans[0]);
        if(ans[1]>0) printf("50 %d\n",ans[1]);
        if(ans[2]>0) printf("100 %d\n",ans[2]);
        if(ans[3]>0) printf("500 %d\n",ans[3]);
