#include <bits/stdc++.h>
using namespace std;
const int INF = 9999;
const int coins[] = {1, 5, 10, 50};

int d[2000];
int c[4];

pair<int, vector<int> > search(int y, vector<int> v = vector<int>(4, 0), int i = 0)
{	
	if (i == 4){
		if (y < 0) return make_pair(INF, vector<int>(0));
		return make_pair(d[y], v);
	}

	pair<int, vector<int> > mini = make_pair(INF, vector<int>(0));

	for (int j = 0; j <= c[i]; j++){
		vector<int> tmp = v;
		tmp[i] = j;
		auto tmpp = search(y + coins[i] * j, tmp, i + 1);
		tmpp.first += c[i] - j;
		mini = min(tmpp, mini);
	}

	return mini;
}

int main()
{	
	for (int i = 1; i < 2000; i++){
		d[i] = INF;
	}

	for (int i = 0; i < 1800; i++){
		for (int j = 0; j < 4; j++){
			d[i + coins[j]] = min(d[i + coins[j]], d[i] + 1);
		}
	}

	int y;
	bool f = false;
	while (scanf("%d", &y), y){
		if (f) puts("");
		f = true;

		for (int i = 0; i < 4; i++){
			scanf("%d", &c[i]);
		}

		vector<int> v = search(-y / 10).second;
		for (int i = 0; i < 4; i++){
			if (!v[i]) continue;
			printf("%d %d\n", coins[i] * 10, v[i]);
		}
	}

	return 0;
}

