#define REP(i, j) for(int i = 0; i < (int)(j); ++i)
#define FOR(i, j, k) for(int i = (int)(j); i < (int)(k); ++i)
#define SORT(v) sort((v).begin(), (v).end())
#define REVERSE(v) reverse((v).begin(), (v).end())
typedef complex<double> P;
const int T = 4;
const int INF = 1e9;

int N, ans, c[4];
vector<int> ans_v;

void check(vector<int> &v, vector<int> &used, int sum){
  int tmp = 0;
  REP(i, T) tmp += v[i];
  sum -= N;
  vector<int> r(T, 0);
  for(int i = T - 1; i >= 0; --i){
    r[i] = sum / c[i];
    sum %= c[i];
    if(used[i] > 0 && r[i] > 0) return ;
    tmp += r[i];
  }
  if(tmp < ans){
    ans = tmp;
    ans_v = used;
  }
}

void dfs(vector<int> &v, vector<int> &used, int sum, set< vector<int> > &S){
  //cout <<"==========" <<endl;
  //cout <<sum <<endl;
  //REP(i, T) cout <<v[i] <<", "; cout <<endl;
  //REP(i, T) cout <<used[i] <<", "; cout <<endl;
  if(sum >= N) check(v, used, sum);
  int cnt = 0;
  REP(i, T) cnt += used[i];
  REP(i, T){
    if(!v[i]) continue;
    v[i] -= 1;
    used[i] += 1;
    if(S.find(used) == S.end()){
      S.insert(used);
      dfs(v, used, sum + c[i], S);
    }
    v[i] += 1;
    used[i] -= 1;
  }
}

