using namespace std;
#define INF INT_MAX

int pay;
int c[4];//10.50.100.500
void init(){
  pay=c[0]=c[1]=c[2]=c[3]=0;
}
bool input(){
  cin>>pay;
  if(pay==0)return false;
  cin>>c[0]>>c[1]>>c[2]>>c[3];
  return true;
}

int toC(int i){
  switch(i){
    case 0:return 10;
    case 1:return 50;
    case 2:return 100;
    case 3:return 500;
    default:return INF;
  }
}


vector<pair<int,int> > solve(){
  vector<pair<int,int> > res;
  vector<int> ans;
  ans.resize(4);
  for(int i=0;i<4;i++){
    int tmp=pay%toC(i+1);
    if(c[i]*toC(i)<tmp){
      ans[i+1]+=1;
      pay-=tmp;
    }else{
      ans[i]+=tmp/toC(i);
      pay-=tmp;
    }
    c[i]-=ans[i];
  }
  ans[0]+=c[0]/5*5;
  ans[1]+=c[1]/2*2;
  ans[2]+=c[2]/5*5;
  for(int i=0;i<4;i++){
    if(ans[i]!=0){
      res.push_back(make_pair(toC(i),ans[i]));
    }
  }
  sort(res.begin(),res.end());
  return res;
}

int main(){
  int cc=0;
  while(init(),input()){
    if(cc!=0){
      cout<<endl;
    }
    cc++;
    vector<pair<int,int> > ans=solve();
    for(int i=0;i<ans.size();i++){
      cout<<ans[i].first<<" "<<ans[i].second<<endl;
    }
  }
}

#include <algorithm>
#include <iostream>
using namespace std;

int money[4] ={10, 50, 100, 500};

int main() {
  while (true) {
    int pay;
    cin >> pay;
    if (pay == 0) return 0;

    cout << endl;

    int coin[4];
    for (int i = 0; i < 4; i++) {
      cin >> coin[i];
    }

    int sum = 0;
    for (int i = 0; i < 4; i++) {
      sum += coin[i] * money[i];
    }

    int extra[4];
    int spare = sum - pay;
    for (int i = 3; i >= 0; i--) {
      int temp = spare / money[i];
      extra[i] = temp;
      spare -= temp * money[i];
    }

    for (int i = 0; i < 4; i++) {
      coin[i] -= extra[i];
      if (coin[i] < 0) coin[i] = 0;
    }

    for (int i = 0; i < 4; i++) {
      if (coin[i]) {
	cout << money[i] << ' ' << coin[i] << endl;
      }
    }
  }
}

