#include<iostream>
using namespace std;
int turi(int n,int i,int j,int k,int l){
	int sum=0;
	if(n<0)return -1;
	while(n>=500){if(l!=0)return -1;n-=500;sum++;}
	while(n>=100){if(k!=0)return -1;n-=100;sum++;}
	while(n>=50){if(j!=0)return -1;n-=50;sum++;}
	while(n>=10){if(i!=0)return -1;n-=10;sum++;}
	return sum;
}
bool f=1;
int main(){
	int n;
	while(true){

		cin>>n;
	if(!f)cout<<endl;//データセットを処理する2番目以降のみ改行
		f=0;
		if(n==0)break;
		int ans[4]={0};
		int table[4]={500,100,50,10};//高い順のテーブル
		int have[4];
		for(int i=0;i<4;i++)cin>>have[i];
		int goke=have[0]+have[1]+have[2]+have[3];
		bool first=1;
		for(int i=0;i<=have[0];i++){
			for(int j=0;j<=have[1];j++){
				for(int k=0;k<=have[2];k++){
					for(int l=0;l<=have[3];l++){
						int now=0;
						if(first==1) now=goke-(ans[0]+ans[1]+ans[2]+ans[3])+turi(ans[0]*10+ans[1]*50+ans[2]*100+ans[3]*500-n,ans[0],ans[1],ans[2],ans[3]);
						else  now=1000000000;
						first=0;
						int nextsum=i*10+j*50+k*100+l*500;
						int next=goke-(i+j+k+l);
						if(nextsum<n)continue;
						if(turi(nextsum-n,i,j,k,l)==-1)continue;
						next+=turi(nextsum-n,i,j,k,l);
						if(next<now){ans[0]=i;ans[1]=j;ans[2]=k;ans[3]=l;}
					}
				}
			}
		}
		for(int i=0;i<4;i++){
		if(ans[i]!=0)cout<<table[3-i]<<' '<<ans[i]<<endl;
		}
