#include <iostream>
#include <vector>
using namespace std;
int item[]={10,50,100,500};
int num[4];
vector<int> sum(4);

int cash(int a){
	int t_num[4];
	int ret = 0;
	for(int i=0;i<4;i++){
		t_num[i] = num[i];
	}
	for(int i=3;i>=0;i--){
		if(a>=item[i] && t_num[i]){
			sum[i]++;
			a-=item[i];
			t_num[i]--;
			ret++;
			i++;
		}
	}
	if(a!=0)return -1;
	return ret;
}


int plz(int a){
	int ret = 0;
	for(int i=3;i>=0;i--){
		if(a>=item[i]){
			if(sum[i])return -1;
			a-=item[i];
			i++;
			ret++;
		}
	}
	return ret;
}

int main(){
	int m,n,t,ret,p=0;
	while(cin >> m && m){
		if(p++)cout << endl;
		pair<int,vector<int> >  ret;
		ret.first = (1<<21);
		for(int i=0;i<4;i++){
			cin >> num[i];
		}
		for(int i=0;i<15000;i++){
			if(~cash(m+i)){
				fill(sum.begin(),sum.end(),0);
				int pre = cash(m+i)+plz(i);
				if(plz(i)==-1)continue;
				ret = min(ret,make_pair(pre,sum));
			}
		}
		for(int i=0;i<4;i++){
			if(ret.second[i])cout << item[i] << " " << ret.second[i] << endl;
		}
	}
}

